#define COL_SKIN vec3(0.95, 0.80, 0.72)
#define COL_EARS COL_SKIN*0.9
#define COL_GLAS vec3(1.00, 0.85, 0.0)
#define COL_BANG vec3(0.50, 0.28, 0.11)
#define COL_HAIR COL_BANG*0.7

#define PI atan(-1.0);

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdSkin( vec2 p )
{

    float distFace = sdCircle(p-vec2(0.5,0.30),0.2);
    float distNeck = sdBox(p-vec2(0.5,0.15),vec2(0.07,0.2));

    return min(distFace,distNeck);
}

float sdEars(vec2 p)
{
    vec2 pEars = p;
    pEars = vec2(abs(pEars.x-0.5),pEars.y);
    return sdCircle(pEars-vec2(0.2,0.25),0.075);
}

float sdBangs(vec2 p)
{
    vec2 pBangs = p;
    pBangs = vec2(abs(pBangs.x-0.5),pBangs.y);
    float bangs11 = sdCircle(pBangs-vec2(0.15,0.5),0.17);
    float bangs12 = sdCircle(pBangs-vec2(0.05,0.35),0.17);
    float bangs1 = max(bangs11,bangs12);

    float bangs21 = sdCircle(pBangs-vec2(0.15,0.48),0.15);
    float bangs22 = sdCircle(pBangs-vec2(0.2,0.55),0.15);
    float bangs2 = max(bangs21,-bangs22);
    bangs2 = bangs2 + float(pBangs.y>0.5)*1.0;

    return min(bangs1,bangs2);
}

float sdHair(vec2 p)
{
    return sdBox(p-vec2(0.5,0.15),vec2(0.22,0.2));  
}

float sdGlasses(vec2 p)
{
    float top = sdBox(p-vec2(0.5,0.33),vec2(0.2,0.0)) - 0.01;

    float bottom = sdBox(p-vec2(0.5,0.30),vec2(0.025,0.0)) - 0.01;

    vec2 pEyes = p;
    pEyes = vec2(abs(pEyes.x-0.5),pEyes.y);
    float eyes = sdCircle(pEyes-vec2(0.1,0.30),0.075);
    eyes = abs(eyes)-0.01;
    eyes = 999.9*float(p.y>0.33)+eyes;

    float dist = min(min(top,bottom),eyes);
    return dist;
}

vec3 swapColorIf(vec3 col1, bool cond, vec3 col2){
    return col1*float(!cond)+col2*float(cond);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float resmax = max(iResolution.x, iResolution.y);
    vec2 st = fragCoord.xy/resmax;

    float angle = atan(st.y-0.3,st.x-0.5);

    vec3 col = vec3(1.0,1.0,0.0)-vec3(0.0,0.5,0.0)*sin(angle*15.0+iTime);

    float distHair = sdHair(st);
    col = swapColorIf(col,distHair<0.0,COL_HAIR);

    float distEars = sdEars(st);
    col = swapColorIf(col,distEars<0.0,COL_EARS);

    float distSkin = sdSkin(st);
    col = swapColorIf(col,distSkin<0.0,COL_SKIN);

    float distGlasses = sdGlasses(st);
    col = swapColorIf(col,distGlasses<0.0,COL_GLAS);

    float distBangs = sdBangs(st);
    col = swapColorIf(col,distBangs<0.0,COL_BANG);


    fragColor = vec4(col,1.0);
}
