void mainImage( out vec4 color, vec2 UVcoords )
{
   float d3 = iResolution.y*.5 ,
         d4 =  iResolution.x*.5 ,
         d2 = 8.0 - 4.0 * sin(iTime*.07 ) + iMouse.y*0.0021,
        d1 = .5;

   UVcoords = .5*(UVcoords - vec2(d4,d3));

#define S(X, Y, period)   color += vec4(sin(length(UVcoords + vec2(X,Y)*d2)*period));

    color = vec4(0.0, 0.0, 0.0, 1.0); // Initialize color with alpha 1

    S(0.0,0.0,iMouse.x*0.01)
    S(0,1.0*d2,d1)  
    S(0,-1.0*d2,d1)  
    S(-1.0*d2,-0.0,d1)  
    S(1.0*d2,0.0,d1)
}
