renderpass.code:
float n = 4.;
mat4 colors = mat4(0.,8.,2.,10.,
                   12.,4.,14.,6.,
                   3.,11.,1.,9.,
                   15.,7.,13.,5.) * (1./16.);
float nearestColor(float color){
    return round(color * n);
}

float dither(float color, float range, float x, float y){
    return nearestColor(color + range * (colors[int(mod(x,n))][int(mod(y,n))]-.5));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = texture(iChannel0,uv).xyz;
    float r = 1./n;
    vec3 dithered = vec3(dither(col.x / n, r, fragCoord.x, fragCoord.y),dither(col.y / n, r, fragCoord.x, fragCoord.y),dither(col.z / n, r, fragCoord.x, fragCoord.y));
    fragColor = vec4(dithered,1.0);
}
