#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float uvoff = .04 * sin( iTime*1.5-PI*.5+PI*2.0*pow(uv.y,5.) * 5.)*.5+.5;

    float wave = sin( -PI*.5+PI*2.0*(uv.x+uvoff) * (8.+uv.y*30.) )*.5+.5;
    wave = pow( wave, 1.+uv.y*10.);
    vec3 col = vec3( wave );

    fragColor = vec4(col,1.0);
}
