void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center1 = vec2(0.3 + 0.1 * cos(iTime), 0.3 + 0.1 * sin(iTime));
    vec2 center2 = vec2(0.5 + 0.1 * cos(iTime + 1.0), 0.4 + 0.1 * sin(iTime + 1.0));
    vec2 center3 = vec2(0.7 + 0.1 * cos(iTime + 2.0), 0.3 + 0.1 * sin(iTime + 2.0));
    vec2 center4 = vec2(0.4 + 0.1 * cos(iTime + 3.0), 0.6 + 0.1 * sin(iTime + 3.0));
    vec2 center5 = vec2(0.6 + 0.1 * cos(iTime + 4.0), 0.6 + 0.1 * sin(iTime + 4.0));

    vec2 centers[5] = vec2[](
        center1,
        center2,
        center3,
        center4,
        center5
    );

    float radii[5] = float[](
        0.15,
        0.1,
        0.2,
        0.05,
        0.1
    );

    vec3 colors[5] = vec3[](
        vec3(0.265,0.890,0.294),
        vec3(0.800,0.484,0.238),
        vec3(0.775,0.319,0.185),
        vec3(0.9, 0.2, 0.1),
        vec3(0.314,0.221,0.900)
    );

    vec3 color = vec3(0.0);

    for (int i = 0; i < 5; i++) {
        float dist = length(fragCoord - centers[i] * iResolution.xy) - radii[i] * min(iResolution.x, iResolution.y);
        if (dist < 0.0) {
            color += colors[i];
            fragColor.a = 0.5;
        }
    }

    fragColor.rgb = mix(vec3(0.044,0.059,0.140), color, step(0.032, color.r + color.g + color.b));
}
