float circle(vec2 p, float r) {
    return length(p) / r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 vuv = fragCoord/iResolution.xy;
    vuv.x *= iResolution.x / iResolution.y;
    vuv.x -= .5;
    vec2 uv = vuv * 2. - 1.;
    vec2 M = (iMouse.xy/iResolution.xy - .5) * 2.;
    float x = sin(iDate.w)*.5+1.;
    if (iMouse.z > 0.001) {
        x = M.x * 2.;
    }
    float c = circle(uv - vec2(x, -.7),.05);

    // Time varying pixel color
    vec3 col = texture(iChannel0, vuv).rgb * (texture(iChannel1, vuv+iDate.p).rgb * 1.-(.5-x));
    if (c < 1.) col *= vec3(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
