const float nb       =  1. ;
const float exponent = 15.1;
const float sMouse   =  4.; 

const float scale    = 10.;
const float speed    = 1000000.;


//const vec2  s = vec2(1, 1.7320508);

//Set mouseControl to true to enable mouse control (duh)
const bool mouseControl = true;

vec3 palette(float t) {

    vec3 a = vec3(   .5,   .8    ,   .5    );
    vec3 b = vec3(   .9,   .5    ,   .5    );
    vec3 c = vec3( 1. ,  5.     , 1.     );
    vec3 d = vec3(   .0,   .33333,   .66666);

    return a
           + b*cos(t) 
           * c*sin(t)
           // + d*cos(1./t)*sin(t)
           ;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    vec2 uv = scale*(fragCoord - .5*iResolution.xy)/iResolution.y;

    float t = iTime;//*speed;

    /*if(mouseControl) {
        vec2 mouse = (iMouse.xy/iResolution.xy) - .5;
        //uv = abs(uv - mouse);
    }
    else {
        uv = abs(uv);
    }*/

    float m = length(uv);
    m = sin(t*m*speed)/nb;
    m = abs(m);


    vec3 finalCol = m *  palette(t*length(sin(m)) );

    fragColor = vec4(finalCol,1.0);
}
