void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = normCoord(fragCoord, iResolution);
    vec2 capp = uv + vec2(0.3, 0.0);
    capp = rotate(-90.0) * capp;
    float d = sdUnevenCapsule(capp, 0.5, 0.1, 1.0);

    d *= -1.0;
    float d2 = d;
    d -= 0.05; //Border thickness

    d = smoothstep(0.0, 0.01, d);
    d2 = smoothstep(0.0, 0.01, d2);

    float border = d2 - d;
    float interior = d;

    fragColor = vec4(interior,border,0.0, 1.0);
}

float pi = 3.141592653589793;

float degToRad(float deg){
    return deg*pi/180.0;
}

vec2 normCoord(vec2 p, vec3 iResolution)
{
    return (2.0*p-iResolution.xy)/iResolution.y;
}

float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

mat2 rotate(float angle){
    angle = degToRad(angle);
    return mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
}
