#define PI 3.14159

vec2 polar(vec2 uv){ return vec2(length(uv), atan(uv.y,uv.x)); }

void mainImage( out vec4 O, in vec2 F )
{
    vec2 uv = (F * 2. - iResolution.xy) / iResolution.y;

    //fov lol
    uv *= 0.4;
    //radializing space
    uv = polar(uv);
    uv.y /= PI/10.;    
    //tunnel effect
    uv.x = 1./uv.x;
    uv.x += iTime;

    //checkerboard pattern
    float d = floor(mod(uv.x, 2.))+floor(mod(uv.y, 2.));
    d = mod(d, 2.);

    O = vec4(vec3(d),0.);
}
