mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;//q=vec3(-1,0,1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,1)*e;
    p.z-=4.;
    p.xz*=rotate2D(iTime*.5);

    for(j=0.;j++<4.;)
        p=abs(abs(p=mod(abs(p),2.))-.05)-.04,
        d=max(max((p.x),log2(p.y)),(p.z)),
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(-log(p/2.),1)*3./i;
}
