#define HPI 1.57079632679
#define PI 3.14159265359
#define TAU 6.28318530718
#define saturate(x) clamp(x, 0.0, 1.0)

#define responseCurve(w, p, v) sin(TAU*saturate((v-p)/w + 0.5) - HPI)*0.5+0.5
vec3 getBlackbodyColor(float t)
{
    vec3 col = vec3(0), m = vec3(0);
    for(float v = 400.; v < 700.; v += 75.)
    {
        vec3 r = vec3(responseCurve(100., 440., v),
                      responseCurve(200., 550., v),
                      responseCurve(200., 600., v));
        m += r;
        col += 2.*v*v*v*(1./(exp(v/t) - 1.))*r;
    }
    return col/m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(sqrt(getBlackbodyColor(uv.x*20.+15.)),1.0);
}
