#define PI 3.14159265
mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t=mod(iTime, 6.);
    vec3 o=vec3(0), rd, p;
    for(float d,y,i,j,g,e,v,R,a,n;i++<1e2;o+=.01/exp(e*1e4/v*vec3(3,2,1)+vec3(4,5,9)/d/1e5)){
        rd=normalize(vec3((.5*iResolution.xy-fragCoord)/iResolution.y,2)),
        p=g*rd+i/4e4;
        p.z-=1.5;
        p.yz*=rotate2D(.6);
        y=p.y;
        e=atan(p.x,p.z);
        p=vec3(log2(v=R=length(p))-t+e/PI,a*sin(4.5*e),a=.3*y/R)+.5;
        for(j=n=0.;j++<9.;p=abs(p/a))
            R*=a=dot(p-=round(p),p)*1.5+.09,
            n+=sin(j/R+t*PI/3.);
        g+=e=min(d=abs(.25*v-y+n*.01)*.4,max(-y,R))*.3;
    }
    o=pow(o,vec3(.3));
    fragColor = vec4(o,1.0);
}
