#define t iTime
#define r iResolution.xy
#define m iMouse.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<5;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p.y-=.05*sin(m.x*t*t)+.5;
        p.x-=.05*cos(m.y*t*t)+.5;
		p.x*=r.x/r.y;
        p.y*=r.y/r.x/.5;
		p.y+=.025*sin(t*3.14);
        p.x+=.025*cos(t*3.14);

		z+=.02*t*t;
		l=length(p);
		uv+=p/l*(sin(z)+1.)*abs(sin(l*9.-z-z));
		c[i]=.01/length(mod(uv,1.)-.5);
	}
	fragColor=vec4(c/l,t*sin(2.*t));
}
