void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseUv = iMouse.xy/iResolution.xy;

    int xDivisions = iMouse.x == 0.0 ? 6 : min(max(2, int(pow(2.0, (1.0 / mouseUv.x) + 0.1))), 100);
    int yDivisions = iMouse.y == 0.0 ? 4 : min(max(2, int(pow(2.0, (1.0 / (1.0 - mouseUv.y)) + 0.1))), 100);

    float xSeg = mod(uv.x * float(xDivisions), 2.0) > 1.0 ? 1.0 : 0.0;
    float ySeg = mod((1.0 - uv.y) * float(yDivisions), 2.0) > 1.0 ? 1.0 : 0.0;
    float seg = xSeg != ySeg ? 1.0 : 0.0;

    if (xSeg == ySeg) {
        fragColor = vec4(vec3(0.0), 1.0);
    } else {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col, 1.0);
    }    
}
