void mainImage( out vec4 O, vec2 I)
{
    //Clear fragcolor
    O *= 0.;
    //Resolution for scaling and centering
    vec2 r = iResolution.xy;
    //Loop 100 times
    for(float i,s; i++<1e2;)
        //Pick disk color
        O += (cos(i*vec4(.1,2,0,0))+1.)/2e2
        //Attenuate using off distance to disk
        /sqrt(max(abs(length((I-r*vec2(.5,s=cos(i+iTime/i)*.3+.5))/
        //"Randomize" radius and width
        vec2(1,.7-s*.7)/r.y)-exp(cos(i/.1)/.1-11.))-sin(i*i/.3)*.1,.003));
}
