float f_n_rand(float n){
    return fract(sin(234.32)*123.3*cos(n*23.23)*23.33);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    if(iMouse.z ==0.){o_trmono = vec2(0.);}
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;
    float n_tau = 6.2831;
    float n_angle = n_tau / 3.;
    float n_its = 50.;
    float n_index = 0.;
    float n_index_nor = 0.;
    float n_dist_clst = 1.;
    float n_t = iTime*2.;
    float n_mixed = 0.;
    float n_dist = 0.5;
    float n_col_nor = 0.;
    float n_dist_mul = 1.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        float n_itnor = n_it / n_its;
        float n_rnd1 = f_n_rand(n_it);
        float n_rnd2 = f_n_rand(n_rnd1);
        vec2 o = o_trpino + vec2(
            sin(n_rnd1*n_tau+iTime)*(iResolution.x/iResolution.y)*0.5,
            cos(n_rnd2*n_tau+iTime)*0.5
            //n_itnor
            //(f_n_rand(n_it+iTime*0.00001)-0.5)*2.,
            //(f_n_rand(n_it+iTime*0.00001)-0.5)*2.
        );
        n_dist = length(o);
        n_dist_mul *= pow(n_dist, 0.2)*1.;
        if(n_dist < n_dist_clst){
            n_dist_clst = n_dist;
        }
    }

    fragColor = vec4(
        n_dist_mul
    );
    if(o_trpino.x>o_trmono.x){
     fragColor = vec4(n_dist_clst*10.);
    }
    fragColor = sqrt(fragColor);

}
