float pixelation = 7.0;

vec2 pixelate(vec2 a, float k){
    float p = pow(2.0,k);
    return floor(a*p)/p;
}

vec4 pixelate(vec4 a, float k){
    float p = pow(2.0,k);
    return floor(a*p)/p;
}

vec4 getcol(vec2 p, vec2 o){
    return pixelate(textureLod(iChannel0,p + o/pow(2.0,pixelation) + iTime*0.005,0.0),3.0);
}

vec4 mincol(vec4 a, vec4 b){
    float A = (0.299*a.r + 0.587*a.g + 0.114*a.b);
    float B = (0.299*b.r + 0.587*b.g + 0.114*b.b);
    return A < B ? a : (B < A ? b : (a.r+a.g+a.b > b.r+b.g+b.b ? a : b));
}

vec4 mincol4(vec4 a, vec4 b, vec4 c, vec4 d){
    return mincol(mincol(a,b),mincol(c,d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y/2.0;
    vec2 uvpx = pixelate(uv,pixelation);
    float pxunit = 1.0/pow(2.0,pixelation);

    vec4 c = getcol(uvpx,vec2(0,0));
    vec4 c0 = getcol(uvpx,vec2(1,0));
    vec4 c1 = getcol(uvpx,vec2(1,1));
    vec4 c2 = getcol(uvpx,vec2(0,1));
    vec4 c3 = getcol(uvpx,vec2(-1,1));
    vec4 c4 = getcol(uvpx,vec2(-1,0));
    vec4 c5 = getcol(uvpx,vec2(-1,-1));
    vec4 c6 = getcol(uvpx,vec2(0,-1));
    vec4 c7 = getcol(uvpx,vec2(1,-1));
    bool u = c2 == c;
    bool d = c6 == c;
    bool l = c4 == c;
    bool r = c0 == c;

    vec2 relcoord = uv - uvpx - pxunit/2.0;
    float dist = length(relcoord);

    if (!(u || l) && relcoord.y > 0.0 && relcoord.x < 0.0 && dist > pxunit/2.0){
        c = c2 == c4 ? c2 : (c == c3 ? c : mincol4(c,c2,c3,c4));
    } else if (!(u || r) && relcoord.y > 0.0 && relcoord.x > 0.0 && dist > pxunit/2.0){
        c = c == c1 ? c : (c2 == c0 ? c2 : mincol4(c,c0,c1,c2));
    } else if (!(d || l) && relcoord.y < 0.0 && relcoord.x < 0.0 && dist > pxunit/2.0){
        c = c == c5 ? c : (c6 == c4 ? c6 : mincol4(c,c4,c5,c6));
    } else if (!(d || r) && relcoord.y < 0.0 && relcoord.x > 0.0 && dist > pxunit/2.0){
        c = c6 == c0 ? c6 : (c == c7 ? c : mincol4(c,c0,c6,c7));
    }

    fragColor = c;
}
