float sdfCircle(vec2 uv, float r, vec2 offset)
{
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    return length(vec2(x,y))-r;
}

vec3 backGroundColor(vec2 uv)
{
    uv.y += 0.5;
    vec3 backgroundStartC = vec3(1.,0.,1.);
    vec3 backgroundEndC = vec3(0.,1.0,1.0);

    return mix(backgroundStartC, backgroundEndC, uv.y);
}

vec3 drawScene(vec2 uv)
{
    vec3 col = vec3(backGroundColor(uv));
    float circle = sdfCircle(uv, 0.1, vec2(0.,0.));
    float x = sin(uv.x*1.5+iTime);
    float circle2 = sdfCircle(uv, 0.1, vec2(x, 0.));

    col = mix(vec3(sin(iTime+uv.xyx+vec3(0,2,4))), col, step(0., circle2));

    col = mix(vec3(cos(iTime+uv.xyx+vec3(0,2,4))), col, step(0., circle));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = drawScene(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
