vec3 random3(vec3 seeds)
{
  seeds = vec3(dot(seeds, vec3(127.1, 311.7, 542.3)),
  dot(seeds, vec3(269.5, 183.3, 461.7)),
  dot(seeds, vec3(732.1, 845.3, 231.7)));
  return fract(sin(seeds) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float l=1.,d=1.,e=.01,v,w,t;
    t = iTime;
    vec2 R;
    R = iResolution.xy;
    vec3 D,B=vec3(.2,1.,1.6),P,I,N,S;
    for (int j;j<9;j++)
    {
        D=normalize(vec3((fragCoord - R*.5)/R.y,.15));
        P = D*l;
        //P.z += t*.2;
        //P*=5.;
        I=floor(P);
        v=w=9.;
          for(int i; i<=64; i++)
          {
              N = vec3(i%4,i/4%4,i/16%4);
              N += -1.5 + .5 * sin(t*.5 + 6.2831 * random3(I+N));
              S = abs(N-P+I);
              d= S.x + S.y + S.z;
              d<v?v=d:d<w?w=d:0.;
          }

        d=step(.02,w-v);
        vec3 col ;
        if(d<e){

          col = B*exp(-l*.2);
        }else{
          col = vec3(0);
        }
        l+=d;
        fragColor = vec4(col,1);
     }
}
