void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float d = length(uv);
    d = pow(d, 0.4);
    float time = iTime + 21.0;

    d = fract((time / 4.5) + 1300.0 * sqrt(d * d) * (d * (fract(tan(d-0.35) * 0.06))));
    d -= abs(uv.y * 1.52) * length(uv);

    float pattern = sin(uv.x * 15.0) * sin(uv.y * 10.0);
    d += pattern * 0.1;

    float blink = abs(sin(mod(sin(time / 10.0),2.9)));
    d *= blink;

    vec3 color;
    if (d < 0.2) {
        color = vec3(abs(mod(5.0 * d, d * d)), d * 0.7, d * 0.4);
    } else if (d < 0.5) {
        color = vec3(d * 0.5, d * 0.7, abs(mod(4.0 * d, d * d)));
    } else {
        color = vec3(d * 0.2, d * 0.5, d * 0.8);
    }

    fragColor = vec4(color.r,color.g,(color.b*2.)+color.r/1.4, 1.0);
}
