void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float Frequency = 5.0; //not what you think
    float Amplitude = 1.5;
    float EpicGamerColorChange = 1.5; //not color, but brightness
    float Triangle = 25.0; //this is a checkerboard bdw. i messed up!
    float Speed = 2.0;

    // Time-dependent animation
    float time = iTime * Speed;

    // Calculate offsets
    vec2 distortion = vec2(
        Amplitude * sin(Frequency * fragCoord.y + time),
        Amplitude * cos(Frequency * fragCoord.x + time)
    );

    // Epic Gamer RGB Moment fail, this is for brightness!
    vec3 color = texture(iChannel0, (fragCoord + distortion) / iResolution.xy).xyz;
    color += EpicGamerColorChange * sin(time);

    // Add some triangle- (well actually it's checkerboard so uh, no triangle ðŸ¤“ðŸ¤“ðŸ¤“) patterns
    float tri = mod(floor(fragCoord.x / Triangle) + floor(fragCoord.y / Triangle), 2.0);
    color *= mix(1.0, 1.5, tri);

    // Output!
    fragColor = vec4(color, 1.0);
}
