// (C) Yury Ershov 2023

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

// (C) Yury Ershov 2023

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iMouse.z > 0.) { RETV(vec4(0.)) }

    ivec2 fragCoord_i = ivec2(fragCoord);
    ivec2 iRes_i = ivec2(iResolution.xy);
    ivec2 iRes_i_1 = iRes_i - ivec2(1, 1);

#if SCROLL_DIR == 1
    fragColor =
        fragCoord_i.y < iRes_i_1.y ?
        T(ivec2(fragCoord_i.x, fragCoord_i.y+1)) :
        vec4(TL(ivec2(fragCoord_i.x, 0)), 1.);
#elif SCROLL_DIR == 2
    fragColor =
        fragCoord_i.y > 0 ?
        T(ivec2(fragCoord_i.x, fragCoord_i.y-1)) :
        vec4(TL(ivec2(fragCoord_i.x, 0)), 1.);
#endif
}

// (C) Yury Ershov 2023

float hash12(vec2 position, float s)  // s=50.
{
    vec2 p = (position + mod(iTime + float(iFrame)/37.9, 200.) * 1500. + s);
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float cc(in vec2 fragCoord, float c, float s) {
    return fract(c + (hash12(fragCoord, s)-0.5)*CSPEED);
}

vec4 mcolr(in vec2 fragCoord, vec4 c, float d) {
    return vec4(cc(fragCoord, c.r, 67.), cc(fragCoord, c.g, 42.), cc(fragCoord, c.b, 92.), c.a + d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 2 || iMouse.z > 0.) { RETV(
#if INITSTYLE == 0
        vec4(0.)
#elif INITSTYLE == 1
        vec4(hash12(fragCoord, 10.), hash12(fragCoord, 20.), hash12(fragCoord, 30.), 1.)
#elif INITSTYLE == 2
        vec4(
            (1. - pow(saw(clamp(fragCoord.x/iResolution.x + 0.5, 0., 1.)) - 1., 2.))/2.,
            (1. - pow(saw(clamp(fragCoord.x/iResolution.x, 0., 1.)) - 1., 2.))/2.,
            (1. - pow(saw(clamp(fragCoord.x/iResolution.x - 0.5, 0., 1.)) - 1., 2.))/2.,
            1.)
#endif
    ) }

    if (fragCoord.y > 1.) {
        RETV(vec4(1., 0., 0., 1.));
        return;
    }

    ivec2 fragCoord_i = ivec2(fragCoord);
    ivec2 iRes_i = ivec2(iResolution.xy);
    ivec2 iRes_i_1 = iRes_i - ivec2(1, 1);

    if (hash12(fragCoord, 50.) > SPEED) { RET }

    vec4 pt = T(ivec2(fragCoord_i.x, 0));
    int sgn = hash12(fragCoord, 55.) > 0.5 ? 1 : -1;
    float d = 1.;
    vec4 pt1 = T(ivec2(fragCoord_i.x + sgn, 0));
    if (pt1.a > pt.a) { pt = pt1; d = SIDE_WEIGHT; }
    vec4 pt2 = T(ivec2(fragCoord_i.x - sgn, 0));
    if (pt2.a > pt.a) { pt = pt2; d = SIDE_WEIGHT; }

    fragColor = mcolr(fragCoord, pt, d);
}

