#define SCROLL_DIR      2   // 1=UP  2=DOWN

#define SPEED         0.5   // speed of horizontal change. 0.5 max
#define CSPEED       0.01   // speed of color change

#define SIDE_WEIGHT   0.8   // 1 or 0.5

#define INITSTYLE       2   // 0=black  1=rnd  2=rainbow







#define T(X)     texelFetch(iChannel0, X, 0)
#define TL(X)    ((saw(texelFetch(iChannel1, X, 0).rgb)+saw(texelFetch(iChannel2, X, 0).rgb)+saw(texelFetch(iChannel3, X, 0).rgb))/3.)
#define DD(X)    ((X).rgb != vec3(0., 0., 0.))
#define D(X)     DD(T(X))
#define RET      fragColor = texelFetch(iChannel0, fragCoord_i, 0); return;
#define RETV(X)  fragColor = X; return;



float saw(float x) { return 1.-abs(fract(x)-0.5)*2.; }
vec3 saw(vec3 x) { return vec3(saw(x.r), saw(x.g), saw(x.b)); }
vec4 saw(vec4 x) { return vec4(saw(x.r), saw(x.g), saw(x.b), x.a); }

