// Editado seguindo algumas sugestões de FabriceNeyret2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = abs( fragCoord - .5*iResolution.xy ) /iResolution.y; 

    float s = uv.x*.7 - uv.y;

    if(s > 0.) 
        uv = uv.yx * .7;

    float fog = min(pow(abs(s),.6), uv.y*uv.y*2.);

    uv.y += .5;
    uv /= .6-uv.y/.7;
    uv *= 3.;
    uv -= .5;
    uv.y -= iTime/2.2;

    uv = floor(uv);

    fragColor = vec4(min(mod(uv.x + uv.y, 2.), fog));
}

