// variant of https://shadertoy.com/view/cl3GDM
// variant of https://shadertoy.com/view/clc3WM
// variant of https://shadertoy.com/view/cl33WM

  #define S(v,s) clamp( v+ cos(s)/abs(sin(s)*min(2.,fwidth(s))), 0., 1. ) // draw AA strips
//#define S(v,s) clamp( v+ cos(s)/fwidth(cos(s)), 0., 1. ) // fwidth(high freq) suffers too much

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y,                  // normalized coordinates
          T = ( U + vec2(-U.y,U) ) *.707,         // 45Â°-tilted ( for cross )
          V = abs( T ),                           // + symmetry 
          A = abs( U ); A.x -= .707;              // symmetries ( for loops )
    float l = length(A), x = A.x/l,
          s = x > -.7 ? sign(U.x)*(.5-l)            : V.y < .2 ? T.y :  T.x, // local parameterization
          t = x > -.7 ? atan(U.y,abs(U.x)-.707)/1.7 : V.y < .2 ? T.x : -T.y;

    O =  1. - clamp(R.yyyy/3. 
               * (.2 - abs( x > -.7               // .2- : thickness
                            ? l - .5              // loops SDF
                            : min(V.x,V.y)        // cross SDF
                        ) ) ,0.,1.)
               *  S(  , 275.*s )                  // AA strips along v loc param
               *  S(1.,  50.*t - 25.*round(43.77*s)/43.77*mod(iTime,11.) );  // AA strips along u loc param
            // *  S( 50.*t - 10.*s - 5.*iTime );  // AA strips along u loc param
}
