struct circle 
{ 
    vec2 pos;
    float r;

};

float circle_render(circle c, vec2 uv)
{
    float f = length(uv - c.pos);
    f = step(c.r, f);
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)*2. / iResolution.y;
    vec2 uv0 = uv;

    circle c1 = circle(vec2(0,-iTime*9.*0.5), 0.2);

    float d = circle_render(c1, uv);
    fragColor = vec4(d,d,d,1.);

}
