/*
by Dom Mandy in 2023
*/
void mainImage(out vec4 pixel, vec2 uv) {
    // layout
    uv = 3. * (2. * uv - iResolution.xy) / iResolution.x;
    uv.x += .6;
    mat2 orbit, trap, shift = mat2(uv, -uv.y, uv);

    // animation
    float t = .5 + .5 * sin(iTime);
    shift = trap = t * shift + (1. - t) * inverse(shift);

    // fractal
    float escape, orbitSize, trapSize = 9.;
    for (; ++escape < 99.;) {
        orbit = orbit * orbit + shift;

        if (4. < (orbitSize = determinant(orbit))) break;
        if (orbitSize < trapSize) trap = orbit, trapSize = orbitSize;

    }

    // orbit trap
    float hue = iTime + atan(trap[0].y, trap[0].x);
    trapSize = smoothstep(-.02, .15, trapSize);

    // palette
    vec3 color = 1. + vec3(.8, .3, .9) * cos(vec3(hue - 2.1, hue, hue + 2.1));
    color *= mix(escape / 15., trapSize, step(99., escape));

    // gamma
    pixel.rgb = pow(color, vec3(1) / 2.2);
}
