void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= 0.5;
    uv.x *= 2.0;

    //vec3 col  = vec3(abs(uv.x * uv.x - uv.y) < 0.01);

    const float lineWidth = 0.01;
    vec3 col = vec3(smoothstep(2.0 / iResolution.y, 0.0, abs(uv.x * uv.x - uv.y) - lineWidth));

    //float f   = uv.x * uv.x - uv.y;
    //vec4 col = vec4( 1. - abs(f) / fwidth(f) );

    // Output to screen
    fragColor = vec4(col,1.0);
}
