void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame!=0){discard;}
    float zoom = 9.;
    vec2 u = zoom*(fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 m = zoom*(iMouse.xy*2.-iResolution.xy)/iResolution.y;
    float t = m.x; //if(iMouse.z<.5){t = iTime*.3;} //how expanded is the wave
          t = 2.;  
    float res = 33.;                               //wave resolution
    float wid = 11.;//66.*m.y;                     //wave width
    float amp = .1;                                //wave amplitude
    float pi  = 3.14159265358;
    float l   = length(u);
    float o   = 0.;
    //O(1) method, top half, exact solution, does not depend on "res"
    float c = 1./wid;
    o = sqrt(pi)/(8.*c*c*c*l)*(-(t - l)*exp(-pow(t - l,2.)/(4.*c*c))
                               +(t + l)*exp(-pow(t + l,2.)/(4.*c*c)));
    //O(n) method, bottom left
    float sum = 0.;
    for(float a=1.; a<res; a++){
        float an1 = a*l;
        float an2 = a*t;
        sum += a*a*cos(an2)*sin(an1)/an1/exp(a*a/wid/wid);
    }
    if(u.x<0.&&u.y<0.){o = sum;}
    //O(n^3) method, bottom right
    sum = 0.;
    for(float c=-res; c<=res; c++){
    for(float b=-res; b<=res; b++){
    for(float a=-res; a<=res; a++){
        float ang = (u.x*a + u.y*b) + sqrt(a*a+b*b+c*c)*t;
        sum += cos(ang)/exp((a*a+b*b+c*c)/wid/wid);
    }}}
    if(u.x>=0.&&u.y<0.){o = sum/pi/4.;}
    fragColor = vec4(o*amp*amp+.5);
}
