void mainImage( out vec4 o, vec2 u )
{
    vec4 v = 16. * u.yxxy / iResolution.y, i;

    for( o = i ; 
        i++.x < 17.; 
         v += o*i/20. -.04*(iTime+60.)*vec4(1,2,3,2) - v.yzwx/16.
       ) 
        o += atan(i+i*cos(i*i+length(.1*v.y+cos(i+v))))/19.;   

    o = 1./cosh(o*o*o*o - sin(v)/5.) ;
}


