float root3 = sqrt(3.0);
float size = 50.;

float voronoiCell(vec2 a){
   vec2 uvTileSize=vec2(2.,2.*root3);
   float frames=mod(iTime,uvTileSize.y);

   float midBelow=distance(a,vec2(uvTileSize.x/2.,frames-uvTileSize.y));
   float midAbove=distance(a,vec2(uvTileSize.x/2.,frames+uvTileSize.y));
   float mid=distance(a,vec2(uvTileSize.x/2.,frames));

   float top_left=distance(a,vec2(.0,1.)*uvTileSize);
   float top_right=distance(a,vec2(1.,1.)*uvTileSize);
   float bottom_right=distance(a,vec2(1.,.0)*uvTileSize);
   float bottom_left=distance(a,vec2(.0)*uvTileSize);

   return sin(min(midBelow,min(midAbove,min(mid,min(top_left,min(top_right,min(bottom_right,bottom_left)))))) * 30.);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
   vec2 uvTileSize=vec2(2.,2.*root3);
   vec2 tileSize=uvTileSize*size;
   vec2 a=mod(fragCoord.xy,tileSize)/tileSize;

   float cell=voronoiCell(a*uvTileSize);

   vec3 cellColour=vec3(cell,.0,1. - cos(cell / 1.));
   float cellAlpha=1.;

   fragColor=vec4(cellColour,cellAlpha);
}
