void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Time variable for animations
    float time = iTime;

    // Normalize the coordinates to the range [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    // Define a background gradient
    vec3 backgroundColor = vec3(0.0, 0.0, 0.0);

    // Create a colorful pattern based on UV coordinates
    vec3 patternColor = vec3(sin(uv.x * 10.0), cos(uv.y * 10.0), abs(sin(time * 0.5)));

    // Combine the background and pattern
    vec3 finalColor = mix(backgroundColor, patternColor, 0.7);

    // Apply some animated effects (e.g., pulsating circles)
    finalColor += 0.2 * cos(length(uv - 0.5) * 15.0 - time) * vec3(1.0, 0.5, 0.5);

    // Output the final color
    fragColor = vec4(finalColor, 1.0);
}
