float R=2.0;
int maxit = 1000;

vec3 oklab_to_linear_srgb(vec3 c)//turn lumanace, a, b into rgb 
{
	float l_ = c.x + 0.3963377774f * c.y + 0.2158037573f * c.z;
	float m_ = c.x - 0.1055613458f * c.y - 0.0638541728f * c.z;
	float s_ = c.x - 0.0894841775f * c.y - 1.2914855480f * c.z;

	float l = l_ * l_ * l_;
	float m = m_ * m_ * m_;
	float s = s_ * s_ * s_;

	return vec3(
		+4.0767416621f * l - 3.3077115913f * m + 0.2309699292f * s,
		-1.2684380046f * l + 2.6097574011f * m - 0.3413193965f * s,
		-0.0041960863f * l - 0.7034186147f * m + 1.7076147010f * s
	);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 seed = (iMouse.xy/iResolution.xy)*2.0*R-R;
    seed.y/=1.777;
    vec2 uv = (fragCoord/iResolution.xy)*2.0*R-R;
    uv.y/=1.777;
    int iteration = 0;
    while(uv.x*uv.x+uv.y*uv.y<R*R && iteration<maxit){
        float temp = uv.x*uv.x-uv.y*uv.y;
        uv.y=2.0*uv.x*uv.y+float(seed.y);
        uv.x= temp +seed.x;
        iteration++;
    }
    if(iteration==maxit){fragColor = vec4(0,0,0,1);}
    else{
        float k = pow(float(iteration)/float(maxit),.20);
        float t = sqrt(uv.x*uv.x+uv.y*uv.y);
        vec3 lab = vec3(1.5*k,uv.x/t/10.0,uv.y/t/10.0);
        fragColor = vec4(oklab_to_linear_srgb(lab),1.0);
    }
}
