mat3x2 base = mat3x2(
    vec2(1.  ,  0.         ),
    vec2(-0.5,  sqrt(3.)/2.),
    vec2(-0.5, -sqrt(3.)/2.));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Centralized-Normalized pixel coordinates
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    // The triangular Coordinate
    vec3 tri = uv*base;

    vec3 col = 0.5 + 0.5*cos(iTime+2.*tri+vec3(0,2,4));

    fragColor = vec4(col, 1.);
}
