void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5, 0.5);


    vec3 col = vec3 ( 0.4, 0.98, 1.0);
    vec3 white = vec3 (1, 1, 1);

    float r = 0.46;
    float s = .2;
    col *= 1.0 - (1.0 - smoothstep (r, 0.5, abs (q.x) ) ) * (1.0 - smoothstep (r, 0.5,  abs (q.y)));
    white *= 0.34 - (1.0 - smoothstep (s, 0.5, abs (q.x) ) ) * (1.0 - smoothstep (s, 0.5,  abs (q.y)));

    vec3 col2 = col + white;

    fragColor = vec4(col2,1.0f);
}
