void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 texCol = texture(iChannel0, uv);


    int xRadius = 5;
    int yRadius = 5;

    vec4 boxSum = vec4(0.);

    for(int x = -xRadius; x <= xRadius; x++){
        for(int y = -yRadius; y <= yRadius; y++){
            vec2 newUV = (fragCoord + vec2(x,y))/iResolution.xy;
            boxSum += texture(iChannel0, newUV);
        }   
    }

    if(uv.x < 0.5){
        fragColor = vec4(texCol);
    } else {
        fragColor = vec4(boxSum / float((2 * xRadius + 1) * (2 * yRadius + 1)));
    }
}
