void mainImage( out vec4 O, vec2 u )
{
    float t = iTime/5.;
    vec2 R = iResolution.xy,
         U = (u+u-R)/R.y,
         r = 4./3.14 * vec2(.5*log(dot(U,U)), atan(U.y, U.x)),
         a = vec2(cos(t), sin(t)),
         C =  mat2(a,-a.y,a.x) * r; 
    C = abs( fract(C) -.5 )/fwidth(C);

    u = u-R/2.;
    O = abs(u.y)<1. && u.x < 0.
         ? vec4(1,0,0,1)
         : vec4( smoothstep(1.5,0., min(C.x,C.y) ) );
}
