#define PI     3.14159265
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    float wavelength=.04;
    vec2 uv = fragCoord/iResolution.y;
    int sources=16;
    float phaseShift=sin(iTime/3.)*2.*PI*5.;
    vec2 offset[16]= vec2[16](
    vec2(0.5,0.65),
    vec2(0.5,0.63),
    vec2(0.5,0.61),
    vec2(0.5,0.59),
    vec2(0.5,0.57),
    vec2(0.5,0.55),
    vec2(0.5,0.53),
    vec2(0.5,0.51),
    vec2(0.5,0.49),
    vec2(0.5,0.47),
    vec2(0.5,0.45),
    vec2(0.5,0.43),
    vec2(0.5,0.41),
    vec2(0.5,0.39),
    vec2(0.5,0.37),
    vec2(0.5,0.35))
    ;
    float a=0.;
    for(int i=0; i<sources;i+=1){
        a+=cos(length(uv.xy-offset[i])/wavelength*2.*PI-iTime*20.+float(i)*phaseShift/float(sources))+1.;
    }
    a=a/float(sources)/2.;
    // Time varying pixel color
    vec3 col = vec3(a, a, a);


    // Output to screen
    fragColor = vec4(col,1.0);
}
