float noise_1D(float uv_x){
    return fract(sin(uv_x*1600.)*5000.);
}


float smoothNoise_1D(float uv_x){
    float lv = fract(uv_x);
    float id = floor(uv_x);

    lv = lv*lv*(3.-2.*lv);

    float left  = noise_1D(id+0.0);
    float right = noise_1D(id+1.0);
    float value = mix(left,right,lv);

    return  value;
}

float fbm(float uv_x,float seed){

    uv_x+=seed;

    float c0;
    c0  = smoothNoise_1D(uv_x*4.);
    c0 += smoothNoise_1D(uv_x*8.)*0.5;
    c0 += smoothNoise_1D(uv_x*16.)*0.25;
    c0 += smoothNoise_1D(uv_x*32.)*0.125;
    c0 += smoothNoise_1D(uv_x*64.)*0.0625;


    c0/=2.;
    return c0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x = uv.x+iTime*0.5;

    float height = fbm(uv.x,100.0);
    float color_h;

    if(uv.y<height){
        color_h = 0.2;
    }else{
        color_h = 0.8;
    }


    vec3 col =vec3(color_h);
    fragColor = vec4(col,1.0);
}
