void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screensize = vec2(1000.);
    float diameter = 1./10.;

    vec2 uv = (fragCoord-iMouse.xy)/screensize;
    vec4 col;

    float l = length(uv)/diameter;
    if (2.*l-1.<0.) {
        fragColor = texture(iChannel1, uv+iMouse.xy/screensize);
        return;
    }
    if (l<1.) {
        float d = atan(uv.y,uv.x);
        l = sqrt(2.*l-1.);
        uv = l*vec2(cos(d),sin(d))*diameter;
    }

    col = texture(iChannel0, uv+iMouse.xy/screensize);


    fragColor = col;
}
