void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalised pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Center UVs
    uv -= 0.5;

    // Normalise
    uv *= 2.0;

    // Aspect ratio fix
    uv.x = uv.x * iResolution.x / iResolution.y;

    // DF
    float dist = length(uv);

    // Ripple
    dist = sin(dist*2.0-(iTime*2.))/2.0;
    dist = abs(dist);

    dist = 0.002 / dist;

    float dist1;

    // Flashes
    dist1 = cos(dist1*3.0-(iTime*3.))/3.0;
    dist1 = abs(dist1);
    dist1 = 0.05 / dist1;

    dist = dist * dist1;

    // Time varying pixel color
    vec3 tint = vec3(2.,3.,1.);

    vec3 col = tint * dist;
    // Output to screen
    fragColor = vec4(col,1.0);

}
