void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col;
    col = texture (iChannel0,uv).rgb;



    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = fragCoord/iResolution.xy;

      vec3 col = texture(iChannel0,uv).rgb;

 if (distance(fragCoord,iMouse.xy)<5.)
      col= vec3 (1);

      else
      col -= vec3 (0.005);

 if (col.x<0.) col=vec3(0.);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uvw = fragCoord/iResolution.xy*6.;


    vec2 uv = fragCoord/iResolution.xy;
          uv += vec2((texture(iChannel1,uvw+vec2(0.037*iTime,0.011*iTime)).rg)-vec2(0.5))/27.;
     vec2 uv1 = fragCoord/iResolution.xy +vec2(0.004,0);
          uv1 += vec2((texture(iChannel1,uvw+vec2(-0.017*iTime,-0.033*iTime)).rg)-vec2(0.5))/27.;
     vec2 uv2 = fragCoord/iResolution.xy +vec2(-0.004,0);
           uv2 += vec2((texture(iChannel1,uvw+vec2(0.13*iTime,-0.46*iTime)).rg)-vec2(0.5))/27.;


    vec3 col;
    vec3 col1;
    vec3 col2;
    col = texture (iChannel0,uv).rgb;
    col1 = texture (iChannel0,uv1).rgb;
    col2 = texture (iChannel0,uv2).rgb;

    col = vec3((col.x+col1.x+col2.x)/4.);

    if (col.x>0.01) col += vec3(.21);




    // Output to screen
    fragColor = vec4(col,1.0);
     //fragColor = vec4(uv,0,1.0);
}
