#define rand(co) fract(sin(dot(floor(co) ,vec2(12.9898,78.233))) * 43758.5453)

vec3 red = vec3(1., .2, .3); 
vec3 white = vec3(1.);
vec3 background = vec3(.8);
vec3 lines = vec3(.9);

float box( vec2 u, float s){
    s = .5 - s*.5;
    u =  smoothstep(s,s+.01, min(u,1.-u));
    return u.x*u.y;
}

void mainImage( out vec4 O, in vec2 u ){
    vec2 i = floor(u *= 0.01),
         g = u - i,
         a = mod(i, 2.) * 2. - 1.;

 float speed = iTime * 2.0;
 float r = rand(u);

 float grid = step(min(g.x,g.y), .01);
 vec2 dash = round(fract(u*14.0));
 grid *= max(dash.x,dash.y);

 float fade = clamp( sin(speed + r * 4.) + .5, .0, 1.);
 float size = ceil(r*4.0)*0.12;
 vec2 pos = g + (.5 - size * .5) * mix(-a, a, fade);
 float boxes = box(pos, size); 
 vec3 col = mix(red, white, fade);
 background = mix(background,lines,grid);
 O.rgb = mix( background, col, boxes);
}
