void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    vec2 z = uv;
    vec2 c = vec2(sin(iTime / 3.) * 4., cos(iTime) * 2.) / 10.;
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + (vec2(dot(c, z), c.y * z.x - c.x * z.y ) / dot(z, z));
        if(dot(z, z) > 65536.0) break;
    } // Divide iterations by maximum
    fragColor = vec4(vec3(pow(l / 100.0, 0.5)) * 1.5 * vec3(0.4, 1.0, 1.3), 1.0);
}
