#define iTime (iTime + 0.2f * hash(iTime, uv))

float hash(float t, vec2 pos) {
    pos = fract(pos*vec2(3.512,7.987));
    pos += dot(pos, pos+t);
    return fract(pos.x*pos.y);
}

//Apply a low-pass filter to the texture to have a smoother noise.
vec3 pinkNoise(vec2 uv) {
    vec3 noise;
    for(int i = 0; i < 9; ++i) {
        noise += texture(iChannel1, uv + vec2(i%3 - 1, i/3 - 1)/iChannelResolution[1].xy).xyz;
    }
    return noise / 9.f;
}

vec2 displacement(vec2 uv, float intensity) {
    vec2 displace1 = 1.4f*(pinkNoise(0.2f*uv + iTime*vec2(0.03f, 0.047f)).xy - vec2(0.5f));
    vec2 displace2 = 0.6f*(pinkNoise(0.2f*uv + iTime*vec2(0.056f, 0.043f)).xy - vec2(0.5f));
    return (displace1 + displace2)*intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.f*fragCoord - iResolution.xy)/iResolution.y;

    vec2 dis = displacement(uv, 0.02f);
    fragColor = texture(iChannel0, uv + dis);

}
