vec2 random(vec2 n)
{
  return fract(sin(dot(n, vec2(127.1, 311.7))) * vec2(43758.5453123, 5453123.43758));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y + iTime * 0.2;
    uv *= 15.0;

    vec2 uv_floor = floor(uv);
    vec2 uv_fract = fract(uv);
    float sum = 1000.0;

    for(int i = -1; i <= 1; i++)
    {
        for(int j = -1; j <= 1; j++)
        {
            vec2 index = vec2(i,j);
            float dst = distance(uv_fract, random(uv_floor + index) + index);
            sum = min(dst, sum);
        }
    }

    fragColor = vec4(vec3(sum),1.0);
}
