void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(0.2, 0.7);

    // setup initial bg color gradient
    vec3 col = mix( vec3(1.0, 0.2, 0.1), vec3(0.5, 0.2, 1.0), sqrt(uv.y));

    // control spinning flower portion
    float r = 0.15 + 0.1 * cos(15.0 * atan(q.y, q.x) + 4.0 * iTime);
    float delta = 0.1 * (0.5 + 0.5 * cos(0.7 * iTime));
    col *= smoothstep(r, r + delta, length(q));

    // control stem
    r = 0.01;
    delta = 0.01;
    col *= 1.0 - (1.0 - smoothstep(r, r + delta, abs(q.x + -0.03*sin(3.0 * q.y)))) * (1.0 - smoothstep(0.0, 0.0 + delta, q.y));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
