vec4 sector_data(vec2 uv, vec2 sector_scale)
{
    // Compute the mean of a sector
    vec2 duv = vec2(1)/iResolution.xy;

    vec3 mean = vec3(0);

    const int samples = 3;
    for(int i=0; i<samples; ++i)
    {
        float dx = duv.x * float(i+1);
        for(int j=0; j<samples; j++)
        {
            float dy = duv.y * float(j+1);
            vec2 duv = vec2(dx, dy) * sector_scale;
            mean += texture(iChannel0, uv + duv).xyz;
        }
    }
    mean /= float(samples*samples);

    // Compute the variance of a sector
    float var = 0.0;
    for(int i=0; i<samples; ++i)
    {
        float dx = duv.x * float(i+1);
        for(int j=0; j<samples; j++)
        {
            float dy = duv.y * float(j+1);
            vec2 duv = vec2(dx, dy) * sector_scale;
            vec3 dcol = texture(iChannel0, uv + duv).xyz - mean;
            var += dot(dcol, dcol);
        }
    }
    //var /= float(samples*samples);
    return vec4(mean, var);
}

