{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 duv = vec2(9)/iResolution.xy;

    /*vec4 sec0 = texture(iChannel1, uv + duv*vec2( 1, 1));//sector_data(uv, vec2( 1, 1));
    vec4 sec1 = texture(iChannel1, uv + duv*vec2(-1, 1));//sector_data(uv, vec2(-1, 1));
    vec4 sec2 = texture(iChannel1, uv + duv*vec2(-1,-1));//sector_data(uv, vec2(-1,-1));
    vec4 sec3 = texture(iChannel1, uv + duv*vec2( 1,-1));//sector_data(uv, vec2( 1,-1));

    vec4 sec = sec0;
    if(sec1.w < sec.w) sec = sec1;
    if(sec2.w < sec.w) sec = sec2;
    if(sec3.w < sec.w) sec = sec3;
    // sec.xyz is now the minimum std of the 4 sectors

    vec3 col = sec.xyz;
    */

    vec4 minsec = vec4(0,0,0,1e30);
    minsec = texture(iChannel1, uv);

    const int sectors = 6;
    for(int i=0; i<sectors; i++)
    {
        const float pi = 3.141592;
        float angle = pi/4.0 + float(i)*2.0*pi/float(sectors) + iTime;
        vec4 sec = texture(iChannel1, uv + duv*vec2(cos(angle), sin(angle)));
        if(sec.w < minsec.w) minsec = sec;
    }

    vec3 col = minsec.xyz;
    //col = texture(iChannel1, uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float kernel(vec2 uv, vec2 duv)
{
    const float sigma = 0.01;//9.0;
    duv /= sigma;
    return exp(-dot(duv, duv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Gaussian blur
    vec2 duv0 = vec2(1)/iResolution.xy;
    const int samples = 21;
    float weight = 0.0;
    for(int i=0; i<samples; ++i)
    {
        float dx = float(i - samples/2);
        for(int j=0; j<samples; j++)
        {
            float dy = float(j - samples/2);
            vec2 duv = vec2(dx, dy) * duv0;
            weight += kernel(uv, duv);
        }
    }

    vec3 mean = vec3(0);
    for(int i=0; i<samples; ++i)
    {
        float dx = float(i - samples/2);
        for(int j=0; j<samples; j++)
        {
            float dy = float(j - samples/2);
            vec2 duv = vec2(dx, dy) * duv0;
            mean += texture(iChannel0, uv + duv).xyz * kernel(uv, duv) / weight;
        }
    }

    // Compute the variance of a sector
    float var = 0.0;
    for(int i=0; i<samples; ++i)
    {
        float dx = float(i - samples/2);
        for(int j=0; j<samples; j++)
        {
            float dy = float(j - samples/2);
            vec2 duv = vec2(dx, dy) * duv0;
            vec3 dcol = texture(iChannel0, uv + duv).xyz - mean;
            var += dot(dcol, dcol) * kernel(uv, duv) / weight;
        }
    }


    // Output to screen
    fragColor = vec4(mean,var);
}
