void mainImage( out vec4 O, vec2 I)
{
    //Clear frag color
    O *= 0.;
    //Initialize iterator and time variables
    float i=0.,f,s=modf(iTime,f);
    //Initialize resolution for scaling
    for(vec2 r=iResolution.xy, c;
    //Loop 8 times
    i++<8.;
    //Skew noise texture
    O+=texture(iChannel0,(ceil(c=(I+I-r)/r.y*mat2(1.155, 0, .577, 1)*exp2(i-s))
    //Offset cells for triangle pattern
    +ceil(c.x-c.y)+f+i)/1e3).r*
    //Compute octave weight
    max(3.5-abs(i-s-3.5),0.));
    //Adjust brightness
    O=O*.1-.1;
}
